import React from "react";
import ReactNotification from "react-notifications-component";
import "react-notifications-component/dist/theme.css";


export default class Register extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            email: "",
            password: "",
            name: ""
        };
        this.notificationDOMRef = React.createRef();
    }

    onSumbit = (event) => {
      let registerData = {
          name: this.state.name,
          password: this.state.password,
          email: this.state.email
      };

      fetch("http://localhost:3001/api/register", {
          method: 'POST',
          body: JSON.stringify(registerData),
          headers: {
              'Content-Type': 'application/json'
          }
      })
          .then(res => {
              if(res.status === 200){
                  this.notificationDOMRef.current.addNotification({
                      title: "Success",
                      message: "Successfully registered!",
                      type: "success",
                      insert: "top",
                      container: "top-center",
                      animationIn: ["animated", "fadeIn"],
                      animationOut: ["animated", "fadeOut"],
                      dismiss: { duration: 2000 },
                      dismissable: { click: true }
                  });
              }
              else if (res.status === 500){
                  
              }
          })

    };

    handleEmailChange = (event) => {
        let email = event.target.value;
        this.setState({
            email: email
        });

    };

    handleNameChange = (event) => {
        let name = event.target.value;
        this.setState({
            name: name
        });
    };

    handlePasswordChange = (event) => {
        let password = event.target.value;
        this.setState({
            password: password
        });
    };

    render(){
        return(
            <div className="container login-container">
                <ReactNotification ref={this.notificationDOMRef}/>
            <div className="row">
                <div className="col-md-12 col-sm-6 login-form-1">
                    <h3>Register to E-Vote</h3>
                    <form>
                        <div className="form-group">
                            <input type="text" name="name" className="form-control" placeholder="Your Name" onChange={this.handleNameChange} />
                        </div>
                        <div className="form-group">
                            <input type="text" name="email" className="form-control" placeholder="Your Email" onChange={this.handleEmailChange}/>
                        </div>
                        <div className="form-group">
                            <input type="password" name="password" className="form-control" placeholder="Your Password" onChange={this.handlePasswordChange}/>
                        </div>
                        <div className="form-group">
                            <input type="password" name="reppassword" className="form-control" placeholder="Repeat Your Password" />
                        </div>
                        <div className="form-group">
                            <input type="submit" className="btnSubmit" value="Register"/>
                        </div>
                    </form>
                </div>

            </div>
        </div>);
    }
}
