import React from 'react';
import ControlledCarousel from './ControlledCarousel';
import ReactNotification from "react-notifications-component";
import Modal from "react-bootstrap/Modal";
import "react-notifications-component/dist/theme.css";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import FormControl from "react-bootstrap/FormControl";
import Button from "react-bootstrap/Button";


export default class App extends React.Component {

    constructor(props, context) {
        super(props, context);

        this.state = {
            smShow: false,
            lgShow: false,
            options: [],
        };
        this.notificationDOMRef = React.createRef();
    }


    openModal = () => {
        const token = sessionStorage.getItem('jwtToken');
        if(token){
            this.setState({ lgShow: true });
        }
        else{
            this.notificationDOMRef.current.addNotification({
                title: "Info",
                message: "You have to be logged in to create a poll! Redirecting to Login page shortly! ",
                type: "warning",
                insert: "top",
                container: "top-center",
                animationIn: ["animated", "fadeIn"],
                animationOut: ["animated", "fadeOut"],
                dismiss: { duration: 2000 },
                dismissable: { click: true }
            });
            setTimeout(() => this.props.history.push('/login'),1500);
        }


    };

    addOptions= () => {
        if(this.textInput.value.localeCompare("") !== 0) {
            let options = this.state.options;
            options.push(
                {
                    name: this.textInput.value,
                    key: this.textInput.value
                }
            );
            this.setState({
                options: options
            });
        }
    };

    deleteItem = (key) => {
      let filteredItems =  this.state.options.filter(item => {
          return (item.key !== key);
      });
      this.setState({
          options: filteredItems
      });
    };


    render() {
        let lgClose = () => this.setState({ lgShow: false });
        return (
            <div>
                <ReactNotification ref={this.notificationDOMRef} />
                <div className="container-fluid p-0">
                    <div className="row">
                        <div className="col-md-12">
                            <ControlledCarousel/>
                        </div>
                    </div>
                </div>
                <div className="container">
                    <div className="row justify-content-md-center">
                        <div className="col-md-6 my-3 d-flex align-items-center flex-column">
                            <h5>To create your own poll, press the following button.</h5>
                            <button type="button" className="btn btn-secondary hvr-bob" onClick={this.openModal}>Create Poll</button>
                        </div>
                    </div>
                </div>
                <div>
                    <Modal
                        size="lg"
                        show={this.state.lgShow}
                        onHide={lgClose}
                        aria-labelledby="example-modal-sizes-title-lg"
                    >
                        <Modal.Header closeButton>
                            <Modal.Title id="example-modal-sizes-title-lg">
                                Create A Poll
                            </Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            <Form>
                                <Form.Group controlId="exampleForm.ControlInput1">
                                    <Form.Label>Poll Title</Form.Label>
                                    <Form.Control type="email" placeholder="Enter title of the poll." />
                                </Form.Group>
                                <Form.Group controlId="exampleForm.ControlSelect1">
                                    <Form.Label>Add Voting Options</Form.Label>
                                    <InputGroup className="mb-3">
                                        <FormControl
                                            placeholder="Recipient's username"
                                            aria-label="Recipient's username"
                                            aria-describedby="basic-addon2"
                                            ref={input => this.textInput = input}
                                        />
                                        <InputGroup.Append>
                                            <Button onClick = {this.addOptions} variant="outline-secondary">Button</Button>
                                        </InputGroup.Append>
                                    </InputGroup>
                                        <ul className="listOptions">
                                            {
                                                this.state.options.map(t => {return <li className="lis hvr-bounce-out" onClick={() => this.deleteItem(t.key)} key={t.key}>{t.name}</li>})
                                            }
                                        </ul>
                                </Form.Group>
                                <Form.Group controlId="exampleForm.ControlTextarea1">
                                    <Form.Label>Description</Form.Label>
                                    <Form.Control as="textarea" rows="3" />
                                </Form.Group>
                                <Button variant="primary" type="submit">
                                    Submit
                                </Button>
                            </Form>
                        </Modal.Body>
                    </Modal>
                </div>
            </div>
                )
        }
}
