import React from 'react';
import "antd/dist/antd.css";
import "./index.css";
import { List, Avatar, Icon, Skeleton } from "antd";



const IconText = ({ type, text }) => (
    <span>
    <Icon type={type} style={{ marginRight: 8 }} />
        {text}
  </span>
);

export default class ListPolls extends React.Component{

    constructor(props){
        super(props);
        this.state={
            loading: false,
            polls: [],
            listData: []
        }
    }

    componentWillMount(){
        this.setState({
            loading: true
        }, () => {
            fetch('http://localhost:3001/api/polls')
                .then(res => {
                    if (res.status === 200) {
                        res.json().then(data => {
                            this.setState({
                                loading: false,
                                polls: data
                            }, () => {this.populateData()});
                        })
                    }
                })
        });

    }

    populateData = () => {
          let listData = [];
          this.state.polls.forEach(item => {
              listData.push({
                  href: "http://ant.design",
                  title: item.title,
                  avatar: "https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png",
                  content: item.description,
                  imageUrl: item.image.url,
                  createdAt: item.createdAt
              });
          });
          this.setState({
              listData: listData
          });

    };



    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col md-12">
                        <Skeleton loading={this.state.loading} active>
                        <List
                            itemLayout="vertical"
                            size="large"
                            pagination={{
                                onChange: page => {
                                    console.log(page);
                                },
                                pageSize: 3
                            }}
                            dataSource={this.state.listData}
                            footer={
                                <div>
                                    <b>ant design</b> footer part
                                </div>
                            }
                            renderItem={item => (
                                <List.Item
                                    key={item.title}
                                    actions={[
                                        <IconText type="star-o" text="156" />,
                                        <IconText type="like-o" text="156" />,
                                        <IconText type="message" text="2" />
                                    ]}
                                    extra={
                                        <a href={item.href}>
                                        <img
                                            width={272}
                                            alt="logo"
                                            src={'http://localhost:3001/api/image/' + item.imageUrl}
                                        />
                                        </a>
                                    }
                                >
                                    <List.Item.Meta
                                        avatar={<Avatar src={item.avatar} />}
                                        title={<a href={item.href}>{item.title}</a>}
                                        description={`Created at: ${Date().valueOf(item.createdAt)}`}
                                    />
                                    {item.content}
                                </List.Item>
                            )}
                        />
                        </Skeleton>
                    </div>
                </div>
            </div>
        );
    }
}
