import React from 'react';
import "antd/dist/antd.css";
import "./index.css";
import { List, Avatar, Icon, Skeleton } from "antd";
import Modal from 'react-bootstrap/Modal';
import Image from 'react-bootstrap/Image';



const IconText = ({ type, text }) => (
    <span>
    <Icon type={type} style={{ marginRight: 8 }} />
        {text}
  </span>
);

const poll =
    {
        "_id": "",
        "author": "",
        "authorId": "",
        "title": "",
        "options": [
            {
                "_id": "",
                "name": "",
                "key": "",
                "votes": 0
            },
            {
                "_id": "",
                "name": "",
                "key": "",
                "votes": 0
            }
        ],
        "expireDate": "",
        "image": {
            "_id": "",
            "url": "",
            "type": ""
        },
        "description": "",
        "likes": 0,
        "comments": [],
        "createdAt": "",
        "updatedAt": "",
        "__v": 0
    };

export default class ListPolls extends React.Component{

    constructor(props){
        super(props);

        this.state={
            loading: false,
            polls: [],
            listData: [],
            visible: false,
            selectedPoll: poll
        }
    }

    componentWillMount(){
        this.setState({
            loading: true
        }, () => {
            fetch('http://localhost:3001/api/polls')
                .then(res => {
                    if (res.status === 200) {
                        res.json().then(data => {
                            this.setState({
                                loading: false,
                                polls: data,
                                selectedPoll: data[0]
                            }, () => {this.populateData()});
                        })
                    }
                })
        });

    }

    populateData = () => {
          let listData = [];
          this.state.polls.forEach(item => {
              listData.push({
                  title: item.title,
                  avatar: "https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png",
                  content: item.description,
                  imageUrl: item.image.url,
                  createdAt: item.createdAt,
                  likes: item.likes,
                  comments: item.comments,
                  id: item._id
              });
          });
          this.setState({
              listData: listData
          });

    };

    handlePollClick = (id) => {
      this.setState({
          visible: true,
          selectedPoll: this.getPollById(id)
      });
    };

    handleOk = () => {
      this.setState({
          visible: false
      })
    };


    handleCancel = () => {
      this.setState({
          visible: false
      })
    };

    getPollById = (id) =>{
      let poll = this.state.polls.filter(poll => {
          return poll._id === id;
      });
      return poll[0];
    };



    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col md-12">
                        <Skeleton loading={this.state.loading} active>
                        <List
                            itemLayout="vertical"
                            size="large"
                            pagination={{
                                onChange: page => {
                                    console.log(page);
                                },
                                pageSize: 3
                            }}
                            dataSource={this.state.listData}
                            footer={
                                <div>
                                    <b>ant design</b> footer part
                                </div>
                            }
                            renderItem={item => (
                                <List.Item
                                    key={item.title}
                                    actions={[
                                        <IconText type="like-o" text={item.likes} />,
                                        <IconText type="message" text= {item.comments.length}/>
                                    ]}
                                    extra={
                                        <button className="invisible-button" onClick={() => this.handlePollClick(item.id)}>
                                        <img
                                            width={272}
                                            alt="logo"
                                            src={'http://localhost:3001/api/image/' + item.imageUrl}
                                        />
                                        </button>
                                    }
                                >
                                    <List.Item.Meta
                                        avatar={<Avatar src={item.avatar} />}
                                        title={<button className="invisible-button" onClick={() => this.handlePollClick(item.id)}>{item.title}</button>}
                                        description={`Created at: ${Date(item.createdAt)}`}
                                    />
                                    {item.content}
                                </List.Item>
                            )}
                        />
                        </Skeleton>
                    </div>
                </div>
                <div className="row">
                    <Modal
                    size="lg"
                    show={this.state.visible}
                    onHide={this.handleCancel}
                    aria-labelledby="example-modal-sizes-title-lg"
                    >
                        <Modal.Header closeButton>
                         <Modal.Title id="example-modal-sizes-title-lg">
                             {this.state.selectedPoll.title}
                            </Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            <div className="container">
                                <div className="row">
                                    <div className="col-md-12">
                                        <Image src={'http://localhost:3001/api/image/' + this.state.selectedPoll.image.url}
                                             alt={this.state.selectedPoll._id}
                                             height={300}
                                             fluid
                                        />
                                        <div className="col-md-12">
                                            <p>{this.state.selectedPoll.description }</p>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </Modal.Body>
                    </Modal>
                </div>
            </div>
        );
    }
}
