import React from 'react';
import ControlledCarousel from './ControlledCarousel';
import Modal from "react-bootstrap/Modal";


export default class App extends React.Component {

    constructor(props, context) {
        super(props, context);

        this.state = {
            smShow: false,
            lgShow: false,
        };
    }

    render() {
        let lgClose = () => this.setState({ lgShow: false });
        return (
            <div>
                <div className="container-fluid p-0">
                    <div className="row">
                        <div className="col-md-12">
                            <ControlledCarousel/>
                        </div>
                    </div>
                </div>
                <div className="container">
                    <div className="row justify-content-md-center">
                        <div className="col-md-6 my-3 d-flex align-items-center flex-column">
                            <h5>To create your own poll, press the following button.</h5>
                            <button type="button" className="btn btn-secondary" onClick={() => this.setState({ lgShow: true })}>Create Poll</button>
                        </div>
                    </div>
                </div>
                <div>
                    <Modal
                        size="sm"
                        show={this.state.smShow}
                        onHide={smClose}
                        aria-labelledby="example-modal-sizes-title-sm"
                    >
                        <Modal.Header closeButton>
                            <Modal.Title id="example-modal-sizes-title-sm">
                                Small Modal
                            </Modal.Title>
                        </Modal.Header>
                        <Modal.Body>...</Modal.Body>
                    </Modal>

                    <Modal
                        size="lg"
                        show={this.state.lgShow}
                        onHide={lgClose}
                        aria-labelledby="example-modal-sizes-title-lg"
                    >
                        <Modal.Header closeButton>
                            <Modal.Title id="example-modal-sizes-title-lg">
                                Large Modal
                            </Modal.Title>
                        </Modal.Header>
                        <Modal.Body>...</Modal.Body>
                    </Modal>
                </div>
            </div>
                )
        }
}
