import React from 'react';
import ControlledCarousel from './ControlledCarousel';
import ReactNotification from "react-notifications-component";
import Modal from "./Modal";
import "react-notifications-component/dist/theme.css";


export default class App extends React.Component {

    constructor(props, context) {
        super(props, context);

        this.state = {
            smShow: false,
            lgShow: false,
        };
        this.notificationDOMRef = React.createRef();
    }


    openModal = () => {
        const token = sessionStorage.getItem('jwtToken');
        if(token){
            this.setState({ lgShow: true });
        }
        else{
            this.notificationDOMRef.current.addNotification({
                title: "Info",
                message: "You have to be logged in to create a poll! Redirecting to Login page shortly! ",
                type: "warning",
                insert: "top",
                container: "top-center",
                animationIn: ["animated", "fadeIn"],
                animationOut: ["animated", "fadeOut"],
                dismiss: { duration: 2000 },
                dismissable: { click: true }
            });
            setTimeout(() => this.props.history.push('/login'),1500);
        }


    };



    render() {

        return (
            <div>
                <ReactNotification ref={this.notificationDOMRef} />
                <div className="container-fluid p-0">
                    <div className="row">
                        <div className="col-md-12">
                            <ControlledCarousel/>
                        </div>
                    </div>
                </div>
                <div className="container">
                    <div className="row justify-content-md-center">
                        <div className="col-md-6 my-3 d-flex align-items-center flex-column">
                            <h5>To create your own poll, press the following button.</h5>
                            <button type="button" className="btn btn-secondary" onClick={this.openModal}>Create Poll</button>
                        </div>
                    </div>
                </div>
                {/*<Modal show={this.state.lgShow}/>*/}
            </div>
                )
        }
}
