import React from "react";
import Toast from "react-bootstrap/Toast";



export default class Login extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            email : "",
            password : "",
            showSuccess: false,
            showFailure: false
        };

    }


    onSubmit = (event) => {
        event.preventDefault();
        let loginData = {
            email: this.state.email,
            password: this.state.password
        };
        fetch("http://localhost:3001/api/authenticate",{
            method: 'POST',
            body: JSON.stringify(loginData),
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(res => {
                if(res.status === 200){
                    res.json().then(data => {
                        sessionStorage.setItem('jwtToken', data.token);
                        console.log("Success");
                        this.setState({
                            showSuccess: true
                        });
                    });

                }
                else if(res.status === 401){
                    res.json().then(data => {
                        console.log(data.error);
                    })
                }
            })
    };

    handleEmailChange = (event) =>{
        let email = event.target.value;
        this.setState({
            email: email
        });
    };

    handlePasswordChange = (event) => {
        let password = event.target.value;
        this.setState({
            password: password
        });
    };

    render(){

        return(

            <div className="container login-container">
                
                <div className="row">
                    <div className="col-md-12 col-sm-6 login-form-1">
                        <h3>Login to E-Vote</h3>
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group">
                                <input type="text" className="form-control" placeholder="Your Email" onChange={this.handleEmailChange}/>
                            </div>
                            <div className="form-group">
                                <input type="password" className="form-control" placeholder="Your Password" onChange={this.handlePasswordChange}/>
                            </div>
                            <div className="form-group">
                                <input type="submit" className="btnSubmit" value="Login"/>
                            </div>
                        </form>
                    </div>

                </div>
            </div>
            );
    }
}
